function updateList(){const e=[...document.querySelectorAll("h1, h2")].sort(((e,t)=>Math.abs(e.getBoundingClientRect().top)-Math.abs(t.getBoundingClientRect().top)));document.querySelectorAll(".selected-circle").forEach((e=>e.classList.remove("selected-circle"))),document.querySelectorAll(".nav-dot")[[...document.querySelectorAll("h1, h2")].indexOf(e[0])].classList.add("selected-circle")}updateList(),window.addEventListener("scroll",(()=>{updateList()}));